/**
 * Bitwise Operation: AND (a & b)
 * @param a Left Operand
 * @param b Right Operand
 */
declare function $band(a: number,b: number): number
/**
 * Bitwise Operation: OR (a | b)
 * @param a Left Operand
 * @param b Right Operand
 */
declare function $bor(a: number,b: number): number
/**
 * Bitwise Operation: XOR (a ^^ b)
 * @param a Left Operand
 * @param b Right Operand
 */
declare function $bxor(a: number,b: number): number
/**
 * Bitwise Operation: NOT (!a or ~a)
 * @param a Operand
 */
declare function $bnot(a: number): number
/**
 * Bitwise Operation: Left shift (a << b)
 * @param a Left Operand
 * @param b Right Operand
 */
declare function $bls(a: number,b: number): number
/**
 * Bitwise Operation: Right Shift (a >> b)
 * @param a Left Operand
 * @param b Right Operand
 */
declare function $brs(a: number,b: number): number